            <div className="border rounded-lg max-h-96 overflow-y-auto">
              <table className="w-full">
                <thead className="bg-gray-50 sticky top-0">
                  <tr>
                    <th className="px-4 py-3 text-left text-sm font-normal text-[#222222]">–§–ò–û</th>
                    <th className="px-4 py-3 text-left text-sm font-normal text-[#222222]">–ö–ª–∞—Å—Å</th>
                    <th className="px-4 py-3 w-12"></th>
