import { createSlice, PayloadAction } from '@reduxjs/toolkit';

export type GroupColor = 'gryffindor' | 'slytherin' | 'ravenclaw' | 'hufflepuff';

export interface Task {
  id: string;
  title: string;
  dateRange: string;
  difficulty: string;
  status: 'completed' | 'in-progress' | 'not-started';
}

export interface Group {
  id: string;
  name: string;
  description: string;
  studentIds: string[];
  createdAt: string;
  color: GroupColor;
  points: number;
  tasks?: Task[];
}

interface GroupsState {
  groups: Group[];
}

const defaultGroups: Group[] = [
  {
    id: '1',
    name: '–ì—Ä–∏—Ñ—Ñ–∏–Ω–¥–æ—Ä',
    description: '–°—Ä–µ–¥–∏ –Ω–∞—à–∏—Ö —É—á–µ–Ω–∏–∫–æ–≤ —Ü–∞—Ä–∏—Ç –¥—É—Ö —Å–º–µ–ª–æ—Å—Ç–∏ –∏ –æ—Ç–≤–∞–≥–∏. –ú—ã –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤—É–µ–º –∏–Ω–∏—Ü–∏–∞—Ç–∏–≤—É –∏ –ø–æ–æ—â—Ä—è–µ–º —Å–∞–º—ã—Ö —è—Ä–∫–∏—Ö –∏ –∞–∫—Ç–∏–≤–Ω—ã—Ö',
    studentIds: ['1', '2', '3'],
    createdAt: new Date().toISOString(),
    color: 'gryffindor',
    points: 1280,
    tasks: [
      {
        id: 't1',
        title: '–û—Ä–≥–∞–Ω–∏–∑–∞—Ü–∏—è —à–∫–æ–ª—å–Ω–æ–≥–æ —Å–ø–µ–∫—Ç–∞–∫–ª—è',
        dateRange: '12.04 - 26.04',
        difficulty: '–®–∫–æ–ª—å–Ω—ã–π —É—Ä–æ–≤–µ–Ω—å',
        status: 'not-started'
      },
      {
        id: 't2',
        title: '–ü—Ä–∏–Ω—è—Ç—å —É—á–∞—Å—Ç–∏–µ –≤ –≤–∏–∫—Ç–æ—Ä–∏–Ω–µ',
        dateRange: '10.04 - 17.04',
        difficulty: '–ú—É–Ω–∏—Ü–∏–ø–∞–ª—å–Ω—ã–π —É—Ä–æ–≤–µ–Ω—å',
        status: 'not-started'
      },
      {
        id: 't3',
        title: '–ü–æ—É—á–∞—Å—Ç–≤–æ–≤–∞—Ç—å –≤–æ –í–ö–û–®–ü',
        dateRange: '10.04 - 17.04',
        difficulty: '–í—Å–µ—Ä–æ—Å—Å–∏–π—Å–∫–∏–π —É—Ä–æ–≤–µ–Ω—å',
        status: 'completed'
      }
    ]
  },
  {
    id: '2',
    name: '–°–ª–∏–∑–µ—Ä–∏–Ω',
    description: '–ú—É–¥—Ä–æ—Å—Ç—å –∏ —Ö–∏—Ç—Ä–æ—Å—Ç—å - –Ω–∞—à–∏ –≥–ª–∞–≤–Ω—ã–µ –∫–∞—á–µ—Å—Ç–≤–∞',
    studentIds: ['4', '5'],
    createdAt: new Date().toISOString(),
    color: 'slytherin',
    points: 1009,
  },
  {
    id: '3',
    name: '–ö–æ–≥—Ç–µ–≤—Ä–∞–Ω',
    description: '–£–º –∏ –∑–Ω–∞–Ω–∏—è –ø—Ä–µ–≤—ã—à–µ –≤—Å–µ–≥–æ',
    studentIds: [],
    createdAt: new Date().toISOString(),
    color: 'ravenclaw',
    points: 963,
  },
];

const loadGroupsFromStorage = (): Group[] => {
  try {
    const saved = localStorage.getItem('hogwartsGroups');
    if (saved) {
      return JSON.parse(saved);
    }
  } catch (error) {
    console.error('Error loading groups from localStorage:', error);
  }
  return defaultGroups;
};

const initialState: GroupsState = {
  groups: loadGroupsFromStorage(),
};

const groupsSlice = createSlice({
  name: 'groups',
  initialState,
  reducers: {
    addGroup: (state, action: PayloadAction<Omit<Group, 'id' | 'createdAt' | 'points'>>) => {
      const newGroup: Group = {
        ...action.payload,
        id: Date.now().toString(),
        createdAt: new Date().toISOString(),
        points: 0,
      };
      state.groups.push(newGroup);
      localStorage.setItem('hogwartsGroups', JSON.stringify(state.groups));
    },
    updateGroup: (state, action: PayloadAction<Group>) => {
      const index = state.groups.findIndex(g => g.id === action.payload.id);
      if (index !== -1) {
        state.groups[index] = action.payload;
        localStorage.setItem('hogwartsGroups', JSON.stringify(state.groups));
      }
    },
    removeGroup: (state, action: PayloadAction<string>) => {
      state.groups = state.groups.filter(group => group.id !== action.payload);
      localStorage.setItem('hogwartsGroups', JSON.stringify(state.groups));
    },
    addStudentToGroup: (state, action: PayloadAction<{ groupId: string; studentId: string }>) => {
      const group = state.groups.find(g => g.id === action.payload.groupId);
      if (group && !group.studentIds.includes(action.payload.studentId)) {
        group.studentIds.push(action.payload.studentId);
        localStorage.setItem('hogwartsGroups', JSON.stringify(state.groups));
      }
    },
    removeStudentFromGroup: (state, action: PayloadAction<{ groupId: string; studentId: string }>) => {
      const group = state.groups.find(g => g.id === action.payload.groupId);
      if (group) {
        group.studentIds = group.studentIds.filter(id => id !== action.payload.studentId);
        localStorage.setItem('hogwartsGroups', JSON.stringify(state.groups));
      }
    },
    updateGroupPoints: (state, action: PayloadAction<{ groupId: string; points: number }>) => {
      const group = state.groups.find(g => g.id === action.payload.groupId);
      if (group) {
        group.points = action.payload.points;
        localStorage.setItem('hogwartsGroups', JSON.stringify(state.groups));
      }
    },
  },
});

export const { addGroup, updateGroup, removeGroup, addStudentToGroup, removeStudentFromGroup, updateGroupPoints } = groupsSlice.actions;
export default groupsSlice.reducer;

